// Replace the UUID with the one of your RollTable
const table = await fromUuid("Compendium.coriolis-modern-combat.tables.RollTable.AWpCdfG2T7r7YbuS");
await table.sheet.render(true);

const tableRoll = await table.roll();
const draws = table.getResultsForRoll(tableRoll.roll.total);
if ( draws.length > 0 ) {
  if ( game.settings.get("core", "animateRollTable") ) {
    await table.sheet._animateRoll(draws);
  }
  await table.draw(tableRoll);
}
setTimeout(async function() {
    await table.sheet.close();
}, 500);
