command:
ui.notifications.info("Starting to revert all weapons back to default Core Rules.");

async function updateItems(items) {
  for (const item of items) {
    if (item.type === 'weapon') {
      if (item.system.special.modernCombat === undefined) continue;
      console.log("Updating:", item.uuid);

      if (item.system.techTier === 'O' || item.system.techTier === 'A') {
        let damage = item.system.damage - (item.system.explosive ? 2 : 1);
        let blastRadius = item.system.blastRadius === "near" ? "close" : item.system.blastRadius;
        let specialFeatures = foundry.utils.deepClone(item.system.special);
        delete specialFeatures.modernCombat;
        if (item.name === "Concussion grenade") {
          damage = 1;
        } else if (item.name === "Frag grenade") {
          damage = 2;
        }
        await item.update({ 'system.special': null });
        await item.update({
          'system.blastRadius': blastRadius,
          'system.damage': damage,
          'system.special': specialFeatures
        });
      }
    }
  }
}

await updateItems(game.items);
for (const actor of game.actors) {
  await updateItems(actor.items);
}
for (const scene of game.scenes) {
  for (const token of scene.tokens) {
    if (!token.actorLink && token.actor) {
      await updateItems(token.actor.items);
    }
  }
}

ui.notifications.info("Finished updating all weapons to Core Rules.");
