command:
if (!_token) {
  ui.notifications.warn(game.i18n.localize("CORIOLISMODERNCOMBAT.SelectToken"));
  return;
}

const effect = await _token.actor.toggleStatusEffect('aiming');
if (effect) {
  ui.notifications.info(game.i18n.format("CORIOLISMODERNCOMBAT.ToggleEffect", { effect: game.i18n.localize("CORIOLISMODERNCOMBAT.EFFECT.Aiming") }));
}
